#importation module mathimport math#creation d'une listeliste=[]#ajout d'un element entier#methode appendliste.append(1)#ajout d'un element reel#methode appendliste.append(math.sqrt(2))#ajout d'une chaine de caracteresliste.append('chaine')#afficher le contenu de la listeprint(liste)#afficher un item i.e. un elements d'une liste#index de 0 au nombre d'elements-1print(liste[2])#modifier un item liste[1]=(1+math.sqrt(5))/2print(liste[1])#supprimer une entree par son indexdel liste[0]print(liste)#supprimer une entree par sa valeurliste.remove("chaine")print(liste)#ajout d'un elementliste.append(7)#inverser les valeurs d'une listeliste.reverse()print(liste)#Compter le nombre d'items/elements d'une listeprint(len(liste))#afficher tous les elements/item d'une listefor elem in liste:    print(elem)    #copier une liste#les deux listes seront independantes#[:] signifie que l'on balaye tous les elements#on n'utilise pas de boucle: operation tres rapidedata=liste[:]#ajout d'un elementsdata.append("today")#concatener deux listes i.e. les mettre pour a boutliste.extend(data)print(liste)